create table if not exists books
(
    id             bigserial not null,
    title          varchar(255)                                                 not null,
    published_date varchar,
    page_count     integer,
    language       varchar(50) default 'en'::character varying                  not null,
    book_id        varchar                                                      not null,
    description    text,
    constraint books_showed_pkey
        primary key (id),
    unique (book_id)
);

comment on table books is 'A list of books showed and who are the actual book';

create table if not exists book_authors
(
    id          bigserial,
    author      varchar(255) not null,
    book_int_id bigint       not null,
    created_at  timestamp    not null,
    primary key (id),
    constraint authors_books_showed_id_fk
        foreign key (book_int_id) references books
            on update cascade on delete cascade
);

create table if not exists book_image_links
(
    id              bigserial,
    book_int_id     bigint    not null,
    small_thumbnail text      not null,
    thumbnail       text      not null,
    created_at      timestamp not null,
    primary key (id),
    constraint book_image_links_books_showed_id_fk
        foreign key (book_int_id) references books
            on update cascade on delete cascade
);

